            const trimmedValue = ctx2.delimiter ? value.split(ctx2.delimiter).map(v => v.trim()) : [value];
            const nextValue = uniq(ctx2.value.concat(...trimmedValue));
            set.value(ctx2, nextValue);
            ctx2.log.prev = ctx2.log.current;
            ctx2.log.current = {
              type: "paste",
              values: trimmedValue
            };
