import { createAnatomy } from "@zag-js/anatomy"

export const anatomy = createAnatomy("avatar").parts("root", "image", "fallback")

export const parts = anatomy.build()
import { createScope } from "@zag-js/dom-query"
import type { MachineContext as Ctx } from "./avatar.types"

export const dom = createScope({
  getRootId: (ctx: Ctx) => ctx.ids?.root ?? `avatar:${ctx.id}`,
  getImageId: (ctx: Ctx) => ctx.ids?.image ?? `avatar:${ctx.id}:image`,
  getFallbackId: (ctx: Ctx) => ctx.ids?.fallback ?? `avatar:${ctx.id}:fallback`,

  getRootEl: (ctx: Ctx) => dom.getById(ctx, dom.getRootId(ctx)),
  getImageEl: (ctx: Ctx) => dom.getById<HTMLImageElement>(ctx, dom.getImageId(ctx)),
})
import type { NormalizeProps, PropTypes } from "@zag-js/types"
import { parts } from "./avatar.anatomy"
import { dom } from "./avatar.dom"
import type { MachineApi, Send, State } from "./avatar.types"

export function connect<T extends PropTypes>(state: State, send: Send, normalize: NormalizeProps<T>): MachineApi<T> {
  const loaded = state.matches("loaded")
  return {
    loaded,
    setSrc(src) {
      send({ type: "SRC.SET", src })
    },
    setLoaded() {
      send({ type: "IMG.LOADED", src: "api" })
    },
    setError() {
      send({ type: "IMG.ERROR", src: "api" })
    },

    getRootProps() {
      return normalize.element({
        ...parts.root.attrs,
        dir: state.context.dir,
        id: dom.getRootId(state.context),
      })
    },

    getImageProps() {
      return normalize.img({
        ...parts.image.attrs,
        hidden: !loaded,
        dir: state.context.dir,
        id: dom.getImageId(state.context),
        "data-state": loaded ? "visible" : "hidden",
        onLoad() {
          send({ type: "IMG.LOADED", src: "element" })
        },
        onError() {
          send({ type: "IMG.ERROR", src: "element" })
        },
      })
    },

    getFallbackProps() {
      return normalize.element({
        ...parts.fallback.attrs,
        dir: state.context.dir,
        id: dom.getFallbackId(state.context),
        hidden: loaded,
        "data-state": loaded ? "hidden" : "visible",
      })
    },
  }
}
import { createMachine } from "@zag-js/core"
import { observeAttributes, observeChildren } from "@zag-js/dom-query"
import { compact } from "@zag-js/utils"
import { dom } from "./avatar.dom"
import type { MachineContext, MachineState, UserDefinedContext } from "./avatar.types"

export function machine(userContext: UserDefinedContext) {
  const ctx = compact(userContext)
  return createMachine<MachineContext, MachineState>(
    {
      id: "avatar",
      initial: "loading",
      activities: ["trackImageRemoval"],

      context: ctx,

      on: {
        "SRC.CHANGE": {
          target: "loading",
        },
        "IMG.UNMOUNT": {
          target: "error",
        },
      },

      states: {
        loading: {
          activities: ["trackSrcChange"],
          entry: ["checkImageStatus"],
          on: {
            "IMG.LOADED": {
              target: "loaded",
              actions: ["invokeOnLoad"],
            },
            "IMG.ERROR": {
              target: "error",
              actions: ["invokeOnError"],
            },
          },
        },
        error: {
          activities: ["trackSrcChange"],
          on: {
            "IMG.LOADED": {
              target: "loaded",
              actions: ["invokeOnLoad"],
            },
          },
        },
        loaded: {
          activities: ["trackSrcChange"],
          on: {
            "IMG.ERROR": {
              target: "error",
              actions: ["invokeOnError"],
            },
          },
        },
      },
    },
    {
      activities: {
        trackSrcChange(ctx, _evt, { send }) {
          const imageEl = dom.getImageEl(ctx)
          return observeAttributes(imageEl, {
            attributes: ["src", "srcset"],
            callback() {
              send({ type: "SRC.CHANGE" })
            },
          })
        },
        trackImageRemoval(ctx, _evt, { send }) {
          const rootEl = dom.getRootEl(ctx)
          return observeChildren(rootEl, {
            callback(records) {
              const removedNodes = Array.from(records[0].removedNodes) as HTMLElement[]
              const removed = removedNodes.find((node) => node.matches("[data-scope=avatar][data-part=image]"))
              if (removed) {
                send({ type: "IMG.UNMOUNT" })
              }
            },
          })
        },
      },
      actions: {
        invokeOnLoad(ctx) {
          ctx.onStatusChange?.({ status: "loaded" })
        },
        invokeOnError(ctx) {
          ctx.onStatusChange?.({ status: "error" })
        },
        checkImageStatus(ctx, _evt, { send }) {
          const imageEl = dom.getImageEl(ctx)
          if (imageEl?.complete) {
            const type = hasLoaded(imageEl) ? "IMG.LOADED" : "IMG.ERROR"
            send({ type, src: "ssr" })
          }
        },
      },
    },
  )
}

function hasLoaded(image: HTMLImageElement) {
  return image.complete && image.naturalWidth !== 0 && image.naturalHeight !== 0
}
import { createProps } from "@zag-js/types"
import { createSplitProps } from "@zag-js/utils"
import type { UserDefinedContext } from "./avatar.types"

export const props = createProps<UserDefinedContext>()(["dir", "id", "ids", "onStatusChange", "getRootNode"])
export const splitProps = createSplitProps<Partial<UserDefinedContext>>(props)
