export function dataURItoBlob(uri: string): Blob {
  const binary = atob(uri.split(",")[1])

  // separate out the mime component
  const mimeString = uri.split(",")[0].split(":")[1].split(";")[0]

  // write the bytes of the string to an ArrayBuffer
  const buffer = new ArrayBuffer(binary.length)

  // create a view into the buffer
  const intArray = new Uint8Array(buffer)

  for (let i = 0; i < binary.length; i++) {
    intArray[i] = binary.charCodeAt(i)
  }

  return new Blob([buffer], { type: mimeString })
}
export function isMSEdge(win: Window): win is Window & { navigator: { msSaveOrOpenBlob: Function } } {
  // @ts-ignore
  return Boolean(win.navigator && win.navigator.msSaveOrOpenBlob)
}

interface DownloadFileOptions {
  /**
   * The name of the file
   */
  name?: string
  /**
   * The MIME type of the file
   */
  type?: string
  /**
   * The file contents
   */
  file: File | Blob | string
  /**
   * The window environment
   */
  win: typeof window
}

export function downloadFile(options: DownloadFileOptions) {
  const { file, win, type, name } = options

  const doc = win.document

  const obj = typeof file === "string" ? new Blob([file], { type }) : file
  const fileName = typeof file === "string" ? name : file instanceof File ? file.name : undefined

  if (isMSEdge(win)) {
    win.navigator.msSaveOrOpenBlob(obj, fileName || "file-download")
    return
  }

  const url = win.URL.createObjectURL(obj)

  const anchor = doc.createElement("a")
  anchor.style.display = "none"
  anchor.href = url
  anchor.rel = "noopener"
  anchor.download = fileName || "file-download"

  doc.documentElement.appendChild(anchor)
  anchor.click()

  setTimeout(() => {
    win.URL.revokeObjectURL(url)
    anchor.remove()
  }, 0)
}
function isMIMEType(v: string) {
  return v === "audio/*" || v === "video/*" || v === "image/*" || v === "text/*" || /\w+\/[-+.\w]+/g.test(v)
}

function isExt(v: string) {
  return /^.*\.[\w]+$/.test(v)
}

const isValidMIME = (v: string) => isMIMEType(v) || isExt(v)

export function getAcceptAttrString(accept: Record<string, string[]> | string | string[] | undefined) {
  if (!accept) return

  if (typeof accept === "string") {
    return accept
  }

  if (Array.isArray(accept)) {
    return accept.filter(isValidMIME).join(",")
  }

  return Object.entries(accept)
    .reduce((a, [mimeType, ext]) => [...a, mimeType, ...ext], [] as string[])
    .filter(isValidMIME)
    .join(",")
}
export const getFileDataUrl = async (file: File | Blob) => {
  const reader = new FileReader()
  return new Promise<string | undefined>((resolve, reject) => {
    reader.onerror = () => {
      reader.abort()
      reject(new Error("There was an error reading a file"))
    }

    reader.onloadend = () => {
      const { result } = reader
      if (result instanceof ArrayBuffer) {
        reject(new Error("Expected DataURL as string from Blob/File, got ArrayBuffer"))
      } else {
        resolve(result || undefined)
      }
    }

    reader.readAsDataURL(file)
  })
}
export const getTotalFileSize = (files: File[]) => {
  return files.reduce((acc, file) => acc + file.size, 0)
}
export const isFileEqual = (file1: File, file2: File) => {
  return file1.name === file2.name && file1.size === file2.size && file1.type === file2.type
}
import type { FileError } from "./types"

const isDefined = <T>(v: T | undefined): v is T => v !== undefined && v !== null

export function isValidFileSize(file: File, minSize?: number, maxSize?: number): [boolean, FileError | null] {
  if (isDefined(file.size)) {
    if (isDefined(minSize) && isDefined(maxSize)) {
      if (file.size > maxSize) return [false, "FILE_TOO_LARGE"]
      if (file.size < minSize) return [false, "FILE_TOO_SMALL"]
    } else if (isDefined(minSize) && file.size < minSize) {
      return [false, "FILE_TOO_SMALL"]
    } else if (isDefined(maxSize) && file.size > maxSize) {
      return [false, "FILE_TOO_LARGE"]
    }
  }
  return [true, null]
}
import type { FileError } from "./types"

function isFileAccepted(file: File | null, accept: string[] | string | undefined) {
  if (file && accept) {
    const types = Array.isArray(accept) ? accept : accept.split(",")

    const fileName = file.name || ""
    const mimeType = (file.type || "").toLowerCase()
    const baseMimeType = mimeType.replace(/\/.*$/, "")

    return types.some((type) => {
      const validType = type.trim().toLowerCase()

      if (validType.charAt(0) === ".") {
        return fileName.toLowerCase().endsWith(validType)
      }

      if (validType.endsWith("/*")) {
        return baseMimeType === validType.replace(/\/.*$/, "")
      }

      return mimeType === validType
    })
  }
  return true
}

export function isValidFileType(file: File, accept: string | undefined): [boolean, FileError | null] {
  const isAcceptable = file.type === "application/x-moz-file" || isFileAccepted(file, accept)
  return [isAcceptable, isAcceptable ? null : "FILE_INVALID_TYPE"]
}
