            actions: ["syncInputValue", "invokeOnClose"]
          }, {
            guard: "isOpenControlled",
            actions: "invokeOnClose"
          }, {
            target: "focused",
            actions: ["invokeOnClose", "setFinalFocus"]
          }],
          "TRIGGER.CLICK": [{
            guard: "isOpenControlled",
            actions: "invokeOnClose"
          }, {
            target: "focused",
            actions: "invokeOnClose"
          }],
          "LAYER.INTERACT_OUTSIDE": [
          // == group 1 ==
          {
            guard: and("isOpenControlled", "isCustomValue", not("allowCustomValue")),
            actions: ["revertInputValue", "invokeOnClose"]
          }, {
            guard: and("isCustomValue", not("allowCustomValue")),
            target: "idle",
            actions: ["revertInputValue", "invokeOnClose"]
          },
          // == group 2 ==
          {
            guard: "isOpenControlled",
            actions: "invokeOnClose"
          }, {
            target: "idle",
            actions: "invokeOnClose"
          }],
          CLOSE: [{
            guard: "isOpenControlled",
            actions: ["invokeOnClose"]
          }, {
            target: "focused",
            actions: ["invokeOnClose", "setFinalFocus"]
          }],
          "VALUE.CLEAR": [{
            guard: "isOpenControlled",
            actions: ["clearInputValue", "clearSelectedItems", "invokeOnClose"]
          }, {
            target: "focused",
            actions: ["clearInputValue", "clearSelectedItems", "invokeOnClose", "setFinalFocus"]
          }]
        }
      },
      suggesting: {
        tags: ["open", "focused"],
        activities: ["trackDismissableLayer", "scrollToHighlightedItem", "computePlacement", "trackChildNodes", "hideOtherElements"],
        entry: ["setInitialFocus"],
        on: {
          "CONTROLLED.CLOSE": [{
            guard: "restoreFocus",
            target: "focused",
            actions: ["setFinalFocus"]
          }, {
            target: "idle"
          }],
          CHILDREN_CHANGE: {
            actions: ["highlightFirstItem"]
          },
          "INPUT.ARROW_DOWN": {
            target: "interacting",
            actions: ["highlightNextItem"]
          },
          "INPUT.ARROW_UP": {
            target: "interacting",
            actions: ["highlightPrevItem"]
          },
          "INPUT.HOME": {
            target: "interacting",
            actions: ["highlightFirstItem"]
          },
          "INPUT.END": {
            target: "interacting",
            actions: ["highlightLastItem"]
          },
          "INPUT.ENTER": [{
            guard: and("isOpenControlled", "closeOnSelect"),
            actions: ["selectHighlightedItem", "invokeOnClose"]
          }, {
            guard: "closeOnSelect",
            target: "focused",
            actions: ["selectHighlightedItem", "invokeOnClose", "setFinalFocus"]
          }, {
            actions: ["selectHighlightedItem"]
          }],
          "INPUT.CHANGE": [{
            guard: "autoHighlight",
            actions: ["setInputValue"]
          }, {
            actions: ["setInputValue"]
          }],
          "LAYER.ESCAPE": [{
            guard: "isOpenControlled",
            actions: ["invokeOnClose"]
          }, {
            target: "focused",
            actions: ["invokeOnClose"]
          }],
          "ITEM.POINTER_MOVE": {
            target: "interacting",
            actions: ["setHighlightedItem"]
          },
          "ITEM.POINTER_LEAVE": {
            actions: ["clearHighlightedItem"]
          },
          "LAYER.INTERACT_OUTSIDE": [
          // == group 1 ==
          {
            guard: and("isOpenControlled", "isCustomValue", not("allowCustomValue")),
            actions: ["revertInputValue", "invokeOnClose"]
          }, {
            guard: and("isCustomValue", not("allowCustomValue")),
            target: "idle",
            actions: ["revertInputValue", "invokeOnClose"]
          },
          // == group 2 ==
          {
            guard: "isOpenControlled",
            actions: ["invokeOnClose"]
          }, {
            target: "idle",
            actions: ["invokeOnClose"]
          }],
          "TRIGGER.CLICK": [{
            guard: "isOpenControlled",
            actions: ["invokeOnClose"]
          }, {
            target: "focused",
            actions: ["invokeOnClose"]
          }],
          "ITEM.CLICK": [{
            guard: and("isOpenControlled", "closeOnSelect"),
            actions: ["selectItem", "invokeOnClose"]
          }, {
            guard: "closeOnSelect",
            target: "focused",
            actions: ["selectItem", "invokeOnClose", "setFinalFocus"]
          }, {
            actions: ["selectItem"]
          }],
          CLOSE: [{
            guard: "isOpenControlled",
            actions: ["invokeOnClose"]
          }, {
            target: "focused",
            actions: ["invokeOnClose", "setFinalFocus"]
          }],
          "VALUE.CLEAR": [{
            guard: "isOpenControlled",
            actions: ["clearInputValue", "clearSelectedItems", "invokeOnClose"]
          }, {
            target: "focused",
            actions: ["clearInputValue", "clearSelectedItems", "invokeOnClose", "setFinalFocus"]
          }]
        }
      }
    }
  }, {
    guards: {
      isInputValueEmpty: ctx2 => ctx2.isInputValueEmpty,
      autoComplete: ctx2 => ctx2.autoComplete && !ctx2.multiple,
      autoHighlight: ctx2 => ctx2.autoHighlight,
      isFirstItemHighlighted: ctx2 => ctx2.collection.firstValue === ctx2.highlightedValue,
      isLastItemHighlighted: ctx2 => ctx2.collection.lastValue === ctx2.highlightedValue,
      isCustomValue: ctx2 => ctx2.inputValue !== ctx2.valueAsString,
      allowCustomValue: ctx2 => !!ctx2.allowCustomValue,
      hasHighlightedItem: ctx2 => ctx2.highlightedValue != null,
      closeOnSelect: ctx2 => !!ctx2.closeOnSelect,
      isOpenControlled: ctx2 => !!ctx2["open.controlled"],
      openOnChange: (ctx2, evt) => {
        if (isBoolean(ctx2.openOnChange)) return ctx2.openOnChange;
        return !!ctx2.openOnChange?.({
          inputValue: evt.value
