      },
      restoreFocus: (_ctx, evt) => evt.restoreFocus == null ? true : !!evt.restoreFocus,
      isChangeEvent: (_ctx, evt) => evt.previousEvent?.type === "INPUT.CHANGE"
    },
    activities: {
      trackDismissableLayer(ctx2, _evt, {
        send
