      },
      clearHighlightedItem(ctx2) {
        set.highlightedValue(ctx2, null, true);
      },
      selectHighlightedItem(ctx2) {
        set.value(ctx2, ctx2.highlightedValue);
      },
      selectItem(ctx2, evt) {
        if (evt.value == null) return;
        set.value(ctx2, evt.value);
      },
      clearItem(ctx2, evt) {
