import { createAnatomy } from "@zag-js/anatomy"

export const anatomy = createAnatomy("progress").parts(
  "root",
  "label",
  "track",
  "range",
  "valueText",
  "view",
  "circle",
  "circleTrack",
  "circleRange",
)

export const parts = anatomy.build()
import { createScope } from "@zag-js/dom-query"
import type { MachineContext as Ctx } from "./progress.types"

export const dom = createScope({
  getRootId: (ctx: Ctx) => ctx.ids?.root ?? `progress-${ctx.id}`,
  getTrackId: (ctx: Ctx) => ctx.ids?.track ?? `progress-${ctx.id}-track`,
  getLabelId: (ctx: Ctx) => ctx.ids?.label ?? `progress-${ctx.id}-label`,
  getCircleId: (ctx: Ctx) => ctx.ids?.circle ?? `progress-${ctx.id}-circle`,
})
import type { NormalizeProps, PropTypes } from "@zag-js/types"
import { parts } from "./progress.anatomy"
import { dom } from "./progress.dom"
import type { MachineApi, MachineContext, ProgressState, Send, State } from "./progress.types"

export function connect<T extends PropTypes>(state: State, send: Send, normalize: NormalizeProps<T>): MachineApi<T> {
  const percent = state.context.percent
  const percentAsString = state.context.isIndeterminate ? "" : `${percent}%`

  const max = state.context.max
  const min = state.context.min

  const orientation = state.context.orientation
  const translations = state.context.translations
  const indeterminate = state.context.isIndeterminate

  const value = state.context.value
  const valueAsString = translations.value({ value, max, percent, min })
  const progressState = getProgressState(value, max)

  const progressbarProps = {
    role: "progressbar",
    "aria-label": valueAsString,
    "data-max": max,
    "aria-valuemin": min,
    "aria-valuemax": max,
    "aria-valuenow": value ?? undefined,
    "data-orientation": orientation,
    "data-state": progressState,
  }

  const circleProps = getCircleProps(state.context)

  return {
    value,
    valueAsString,
    min,
    max,
    percent,
    percentAsString,
    indeterminate,
    setValue(value) {
      send({ type: "VALUE.SET", value })
    },
    setToMax() {
      send({ type: "VALUE.SET", value: max })
    },
    setToMin() {
      send({ type: "VALUE.SET", value: min })
    },

    getRootProps() {
      return normalize.element({
        dir: state.context.dir,
        ...parts.root.attrs,
        id: dom.getRootId(state.context),
        "data-max": max,
        "data-value": value ?? undefined,
        "data-state": progressState,
        "data-orientation": orientation,
        style: {
          "--percent": indeterminate ? undefined : percent,
        },
      })
    },

    getLabelProps() {
      return normalize.element({
        dir: state.context.dir,
        id: dom.getLabelId(state.context),
        ...parts.label.attrs,
        "data-orientation": orientation,
      })
    },

    getValueTextProps() {
      return normalize.element({
        dir: state.context.dir,
        "aria-live": "polite",
        ...parts.valueText.attrs,
      })
    },

    getTrackProps() {
      return normalize.element({
        dir: state.context.dir,
        id: dom.getTrackId(state.context),
        ...parts.track.attrs,
        ...progressbarProps,
      })
    },

    getRangeProps() {
      return normalize.element({
        dir: state.context.dir,
        ...parts.range.attrs,
        "data-orientation": orientation,
        "data-state": progressState,
        style: {
          [state.context.isHorizontal ? "width" : "height"]: indeterminate ? undefined : `${percent}%`,
        },
      })
    },

    getCircleProps() {
      return normalize.element({
        dir: state.context.dir,
        id: dom.getCircleId(state.context),
        ...parts.circle.attrs,
        ...progressbarProps,
        ...circleProps.root,
      })
    },

    getCircleTrackProps() {
      return normalize.element({
        dir: state.context.dir,
        "data-orientation": orientation,
        ...parts.circleTrack.attrs,
        ...circleProps.track,
      })
    },

    getCircleRangeProps() {
      return normalize.element({
        dir: state.context.dir,
        ...parts.circleRange.attrs,
        ...circleProps.range,
        "data-state": progressState,
      })
    },

    getViewProps(props) {
      return normalize.element({
        dir: state.context.dir,
        ...parts.view.attrs,
        "data-state": props.state,
        hidden: props.state !== progressState,
      })
    },
  }
}

function getProgressState(value: number | null, maxValue: number): ProgressState {
  return value == null ? "indeterminate" : value === maxValue ? "complete" : "loading"
}

function getCircleProps(ctx: MachineContext) {
  const circleProps = {
    style: {
      "--radius": "calc(var(--size) / 2 - var(--thickness) / 2)",
      cx: "calc(var(--size) / 2)",
      cy: "calc(var(--size) / 2)",
      r: "var(--radius)",
      fill: "transparent",
      strokeWidth: "var(--thickness)",
    },
  }
  return {
    root: {
      style: {
        width: "var(--size)",
        height: "var(--size)",
      },
    },
    track: circleProps,
    range: {
      opacity: ctx.value === 0 ? 0 : undefined,
      style: {
        ...circleProps.style,
        "--percent": ctx.percent,
        "--circumference": `calc(2 * 3.14159 * var(--radius))`,
        "--offset": `calc(var(--circumference) * (100 - var(--percent)) / 100)`,
        strokeDashoffset: `calc(var(--circumference) * ((100 - var(--percent)) / 100))`,
        strokeDasharray: ctx.isIndeterminate ? undefined : `var(--circumference)`,
        transformOrigin: "center",
        transform: "rotate(-90deg)",
      },
    },
  }
}
import { createMachine } from "@zag-js/core"
import { compact, isNumber } from "@zag-js/utils"
import type { MachineContext, MachineState, UserDefinedContext } from "./progress.types"

function midValue(min: number, max: number) {
  return min + (max - min) / 2
}

export function machine(userContext: UserDefinedContext) {
  const ctx = compact(userContext)
  return createMachine<MachineContext, MachineState>(
    {
      id: "progress",
      initial: "idle",
      context: {
        max: ctx.max ?? 100,
        min: ctx.min ?? 0,
        value: midValue(ctx.min ?? 0, ctx.max ?? 100),
        orientation: "horizontal",
        translations: {
          value: ({ percent }) => (percent === -1 ? "loading..." : `${percent} percent`),
          ...ctx.translations,
        },
        ...ctx,
      },

      created: ["validateContext"],

      computed: {
        isIndeterminate: (ctx) => ctx.value === null,
        percent(ctx) {
          if (!isNumber(ctx.value)) return -1
          return Math.round(((ctx.value - ctx.min) / (ctx.max - ctx.min)) * 100)
        },
        isAtMax: (ctx) => ctx.value === ctx.max,
        isHorizontal: (ctx) => ctx.orientation === "horizontal",
        isRtl: (ctx) => ctx.dir === "rtl",
      },

      states: {
        idle: {
          on: {
            "VALUE.SET": {
              actions: ["setValue"],
            },
          },
        },
      },
    },
    {
      actions: {
        setValue: (ctx, evt) => {
          ctx.value = evt.value === null ? null : Math.max(0, Math.min(evt.value, ctx.max))
        },
        validateContext: (ctx) => {
          if (ctx.value == null) return

          if (!isValidNumber(ctx.max)) {
            throw new Error(`[progress] The max value passed \`${ctx.max}\` is not a valid number`)
          }

          if (!isValidMax(ctx.value, ctx.max)) {
            throw new Error(`[progress] The value passed \`${ctx.value}\` exceeds the max value \`${ctx.max}\``)
          }

          if (!isValidMin(ctx.value, ctx.min)) {
            throw new Error(`[progress] The value passed \`${ctx.value}\` exceeds the min value \`${ctx.min}\``)
          }
        },
      },
    },
  )
}

function isValidNumber(max: any) {
  return isNumber(max) && !isNaN(max)
}

function isValidMax(value: number, max: number) {
  return isValidNumber(value) && value <= max
}

function isValidMin(value: number, min: number) {
  return isValidNumber(value) && value >= min
}
import { createProps } from "@zag-js/types"
import { createSplitProps } from "@zag-js/utils"
import type { UserDefinedContext } from "./progress.types"

export const props = createProps<UserDefinedContext>()([
  "dir",
  "getRootNode",
  "id",
  "ids",
  "max",
  "min",
  "orientation",
  "translations",
  "value",
])

export const splitProps = createSplitProps<Partial<UserDefinedContext>>(props)
