import { createAnatomy } from "@zag-js/anatomy"

export const anatomy = createAnatomy("accordion").parts("root", "item", "itemTrigger", "itemContent", "itemIndicator")
export const parts = anatomy.build()
import { getEventKey, type EventKeyMap } from "@zag-js/dom-event"
import { dataAttr, isSafari } from "@zag-js/dom-query"
import type { NormalizeProps, PropTypes } from "@zag-js/types"
import { parts } from "./accordion.anatomy"
import { dom } from "./accordion.dom"
import type { ItemProps, ItemState, MachineApi, Send, State } from "./accordion.types"

export function connect<T extends PropTypes>(state: State, send: Send, normalize: NormalizeProps<T>): MachineApi<T> {
  const focusedValue = state.context.focusedValue
  const value = state.context.value
  const multiple = state.context.multiple

  function setValue(value: string[]) {
    let nextValue = value
    if (multiple && nextValue.length > 1) {
      nextValue = [nextValue[0]]
    }
    send({ type: "VALUE.SET", value: nextValue })
  }

  function getItemState(props: ItemProps): ItemState {
    return {
      expanded: value.includes(props.value),
      focused: focusedValue === props.value,
      disabled: Boolean(props.disabled ?? state.context.disabled),
    }
  }

  return {
    focusedValue,
    value,
    setValue,
    getItemState,

    getRootProps() {
      return normalize.element({
        ...parts.root.attrs,
        dir: state.context.dir,
        id: dom.getRootId(state.context),
        "data-orientation": state.context.orientation,
      })
    },

    getItemProps(props: ItemProps) {
      const itemState = getItemState(props)
      return normalize.element({
        ...parts.item.attrs,
        dir: state.context.dir,
        id: dom.getItemId(state.context, props.value),
        "data-state": itemState.expanded ? "open" : "closed",
        "data-focus": dataAttr(itemState.focused),
        "data-disabled": dataAttr(itemState.disabled),
        "data-orientation": state.context.orientation,
      })
    },

    getItemContentProps(props: ItemProps) {
      const itemState = getItemState(props)
      return normalize.element({
        ...parts.itemContent.attrs,
        dir: state.context.dir,
        role: "region",
        id: dom.getItemContentId(state.context, props.value),
        "aria-labelledby": dom.getItemTriggerId(state.context, props.value),
        hidden: !itemState.expanded,
        "data-state": itemState.expanded ? "open" : "closed",
        "data-disabled": dataAttr(itemState.disabled),
        "data-focus": dataAttr(itemState.focused),
        "data-orientation": state.context.orientation,
      })
    },

    getItemIndicatorProps(props) {
      const itemState = getItemState(props)
      return normalize.element({
        ...parts.itemIndicator.attrs,
        dir: state.context.dir,
        "aria-hidden": true,
        "data-state": itemState.expanded ? "open" : "closed",
        "data-disabled": dataAttr(itemState.disabled),
        "data-focus": dataAttr(itemState.focused),
        "data-orientation": state.context.orientation,
      })
    },

    getItemTriggerProps(props: ItemProps) {
      const { value } = props
      const itemState = getItemState(props)

      return normalize.button({
        ...parts.itemTrigger.attrs,
        type: "button",
        dir: state.context.dir,
        id: dom.getItemTriggerId(state.context, value),
        "aria-controls": dom.getItemContentId(state.context, value),
        "aria-expanded": itemState.expanded,
        disabled: itemState.disabled,
        "data-orientation": state.context.orientation,
        "aria-disabled": itemState.disabled,
        "data-state": itemState.expanded ? "open" : "closed",
        "data-ownedby": dom.getRootId(state.context),
        onFocus() {
          if (itemState.disabled) return
          send({ type: "TRIGGER.FOCUS", value })
        },
        onBlur() {
          if (itemState.disabled) return
          send("TRIGGER.BLUR")
        },
        onClick(event) {
          if (itemState.disabled) return
          if (isSafari()) {
            event.currentTarget.focus()
          }
          send({ type: "TRIGGER.CLICK", value })
        },
        onKeyDown(event) {
          if (event.defaultPrevented) return
          if (itemState.disabled) return

          const keyMap: EventKeyMap = {
            ArrowDown() {
              if (state.context.isHorizontal) return
              send({ type: "GOTO.NEXT", value })
            },
            ArrowUp() {
              if (state.context.isHorizontal) return
              send({ type: "GOTO.PREV", value })
            },
            ArrowRight() {
              if (!state.context.isHorizontal) return
              send({ type: "GOTO.NEXT", value })
            },
            ArrowLeft() {
              if (!state.context.isHorizontal) return
              send({ type: "GOTO.PREV", value })
            },
            Home() {
              send({ type: "GOTO.FIRST", value })
            },
            End() {
              send({ type: "GOTO.LAST", value })
            },
          }

          const key = getEventKey(event, {
            dir: state.context.dir,
            orientation: state.context.orientation,
          })

          const exec = keyMap[key]

          if (exec) {
            exec(event)
            event.preventDefault()
          }
        },
      })
    },
  }
}
import { nextById, prevById, queryAll, createScope } from "@zag-js/dom-query"
import { first, last } from "@zag-js/utils"
import type { MachineContext as Ctx } from "./accordion.types"

export const dom = createScope({
  getRootId: (ctx: Ctx) => ctx.ids?.root ?? `accordion:${ctx.id}`,
  getItemId: (ctx: Ctx, value: string) => ctx.ids?.item?.(value) ?? `accordion:${ctx.id}:item:${value}`,
  getItemContentId: (ctx: Ctx, value: string) =>
    ctx.ids?.itemContent?.(value) ?? `accordion:${ctx.id}:content:${value}`,
  getItemTriggerId: (ctx: Ctx, value: string) =>
    ctx.ids?.itemTrigger?.(value) ?? `accordion:${ctx.id}:trigger:${value}`,

  getRootEl: (ctx: Ctx) => dom.getById(ctx, dom.getRootId(ctx)),
  getTriggerEls: (ctx: Ctx) => {
    const ownerId = CSS.escape(dom.getRootId(ctx))
    const selector = `[aria-controls][data-ownedby='${ownerId}']:not([disabled])`
    return queryAll(dom.getRootEl(ctx), selector)
  },

  getFirstTriggerEl: (ctx: Ctx) => first(dom.getTriggerEls(ctx)),
  getLastTriggerEl: (ctx: Ctx) => last(dom.getTriggerEls(ctx)),
  getNextTriggerEl: (ctx: Ctx, id: string) => nextById(dom.getTriggerEls(ctx), dom.getItemTriggerId(ctx, id)),
  getPrevTriggerEl: (ctx: Ctx, id: string) => prevById(dom.getTriggerEls(ctx), dom.getItemTriggerId(ctx, id)),
})
import { createMachine, guards } from "@zag-js/core"
import { add, compact, isEqual, remove, warn } from "@zag-js/utils"
import { dom } from "./accordion.dom"
import type { MachineContext, MachineState, UserDefinedContext } from "./accordion.types"

const { and, not } = guards

export function machine(userContext: UserDefinedContext) {
  const ctx = compact(userContext)
  return createMachine<MachineContext, MachineState>(
    {
      id: "accordion",
      initial: "idle",

      context: {
        focusedValue: null,
        value: [],
        collapsible: false,
        multiple: false,
        orientation: "vertical",
        ...ctx,
      },

      watch: {
        value: "coarseValue",
        multiple: "coarseValue",
      },

      created: "coarseValue",

      computed: {
        isHorizontal: (ctx) => ctx.orientation === "horizontal",
      },

      on: {
        "VALUE.SET": {
          actions: ["setValue"],
        },
      },

      states: {
        idle: {
          on: {
            "TRIGGER.FOCUS": {
              target: "focused",
              actions: "setFocusedValue",
            },
          },
        },
        focused: {
          on: {
            "GOTO.NEXT": {
              actions: "focusNextTrigger",
            },
            "GOTO.PREV": {
              actions: "focusPrevTrigger",
            },
            "TRIGGER.CLICK": [
              {
                guard: and("isExpanded", "canToggle"),
                actions: ["collapse"],
              },
              {
                guard: not("isExpanded"),
                actions: ["expand"],
              },
            ],
            "GOTO.FIRST": {
              actions: "focusFirstTrigger",
            },
            "GOTO.LAST": {
              actions: "focusLastTrigger",
            },
            "TRIGGER.BLUR": {
              target: "idle",
              actions: "clearFocusedValue",
            },
          },
        },
      },
    },
    {
      guards: {
        canToggle: (ctx) => !!ctx.collapsible || !!ctx.multiple,
        isExpanded: (ctx, evt) => ctx.value.includes(evt.value),
      },
      actions: {
        collapse(ctx, evt) {
          const next = ctx.multiple ? remove(ctx.value, evt.value) : []
          set.value(ctx, ctx.multiple ? next : [])
        },
        expand(ctx, evt) {
          const next = ctx.multiple ? add(ctx.value, evt.value) : [evt.value]
          set.value(ctx, next)
        },
        focusFirstTrigger(ctx) {
          dom.getFirstTriggerEl(ctx)?.focus()
        },
        focusLastTrigger(ctx) {
          dom.getLastTriggerEl(ctx)?.focus()
        },
        focusNextTrigger(ctx) {
          if (!ctx.focusedValue) return
          const triggerEl = dom.getNextTriggerEl(ctx, ctx.focusedValue)
          triggerEl?.focus()
        },
        focusPrevTrigger(ctx) {
          if (!ctx.focusedValue) return
          const triggerEl = dom.getPrevTriggerEl(ctx, ctx.focusedValue)
          triggerEl?.focus()
        },
        setFocusedValue(ctx, evt) {
          set.focusedValue(ctx, evt.value)
        },
        clearFocusedValue(ctx) {
          set.focusedValue(ctx, null)
        },
        setValue(ctx, evt) {
          set.value(ctx, evt.value)
        },
        coarseValue(ctx) {
          if (!ctx.multiple && ctx.value.length > 1) {
            warn(`The value of accordion should be a single value when multiple is false.`)
            ctx.value = [ctx.value[0]]
          }
        },
      },
    },
  )
}

const invoke = {
  change(ctx: MachineContext) {
    ctx.onValueChange?.({ value: Array.from(ctx.value) })
  },
  focusChange(ctx: MachineContext) {
    ctx.onFocusChange?.({ value: ctx.focusedValue })
  },
}

const set = {
  value(ctx: MachineContext, value: string[]) {
    if (isEqual(ctx.value, value)) return
    ctx.value = value
    invoke.change(ctx)
  },
  focusedValue(ctx: MachineContext, value: string | null) {
    if (isEqual(ctx.focusedValue, value)) return
    ctx.focusedValue = value
    invoke.focusChange(ctx)
  },
}
